        if(ni[i]%in%v){
          if(ni[j]%in%v){}
          else{v=c(v,ni[j])}
        }else(
          if(ni[j]%in%v){v=c(v,ni[i])}
          else{v=c(v,c(ni[i],ni[j]))}
        )
      }
    }
  }
  if(is.null(v)==TRUE){
    #print(paste('no such ni found',t))
    }
  else{
    subset_dat=NULL
    for(i in v){subset_dat=rbind(subset_dat,subset(data_miu,Ni==i))}
    v1=subset_dat$Ni
    len=length(v1)
    subset_dat1=NULL
    for(i in 1:(len-1)){
      for(j in (i+1):len){
        if(v1[i]+v1[j]==n){subset_dat1=rbind(subset_dat1,c(subset_dat$Ni[i],subset_dat$Ni[j],subset_dat$kii[i],subset_dat$kii[j],subset_dat$ei[i],subset_dat$ei[j],subset_dat$fi[i],subset_dat$fi[j],subset_dat$lambdai[i],subset_dat$lambdai[j]))}
      }
    } 
    subset_dat1
    colnames(subset_dat1)=c('n1','n2','k11','k22','e1','e2','f1','f2','lambda1','lambda2')
    lambda=subset_dat1[,'lambda1']+subset_dat1[,'lambda2']-mu
    subset=cbind(subset_dat1,lambda)
    p=-( (t-1)*lambda+0.5*t*(t-1)*mu)
    subset=cbind(subset,p)
    
    #
    #check=(p+mu==subset[,'k11']+subset[,'k22'])
    #if(sum(check)==0){print(paste('contradiction',t,'p=',p,'l=',lambda,'k11=',subset[,'k11'],'k22=',subset[,'k22'],.5*t*(-mu)-lambda,subset[,'f1']+subset[,'f2']))}
    #if(sum(check)!=0){print(paste('eqn holds',t,'p=',p,'l=',lambda,'k11=',subset[,'k11'],'k22=',subset[,'k22'],.5*t*(-mu)-lambda,subset[,'f1']+subset[,'f2']))}
    #
    
    k=NULL
    for(i in length(p)){
      k21=sqrt((subset[i,'k11']*subset[i,'k22']-subset[i,'p']*mu)*subset[i,'n1']/subset[i,'n2'])
      k=c(k,k21)
    }
    if(rational(k,cycles=1000,max.denominator = Inf)!='NaN'){
      print(paste('rational found',t,k))
      if(sum(k%%1==0)==0){print('k21 not integer')
        } #none is integer
      if(sum(k%%1==0)!=0){
        #print(paste('found',t))
